const setLanguage = (lang) => {
  document.querySelectorAll('[data-key]').forEach(element => {
    const key = element.getAttribute('data-key');
    if (translations[lang] && translations[lang][key]) {
      element.textContent = translations[lang][key];
    }
  });
  
  document.documentElement.lang = lang;
  localStorage.setItem('lang', lang);
  document.getElementById('current-lang-text').textContent = lang.toUpperCase();
  
  document.querySelectorAll('.dropdown-item').forEach(item => {
    item.classList.toggle('active', item.getAttribute('data-lang') === lang);
  });
};

document.addEventListener('DOMContentLoaded', () => {
  const dropdown = document.querySelector('.dropdown-lang');
  const toggle = dropdown.querySelector('.dropdown-toggle');
  const items = dropdown.querySelectorAll('.dropdown-item');
  
  toggle.addEventListener('click', (e) => {
    e.stopPropagation();
    dropdown.classList.toggle('open');
  });
  
  document.addEventListener('click', () => {
    if (dropdown.classList.contains('open')) {
      dropdown.classList.remove('open');
    }
  });
  
  items.forEach(item => {
    item.addEventListener('click', (e) => {
      e.preventDefault();
      const lang = item.getAttribute('data-lang');
      setLanguage(lang);
      dropdown.classList.remove('open');
    });
  });
  
  setLanguage(localStorage.getItem('lang') || 'en');
  
  const observerOptions = {
    threshold: 0.1,
    rootMargin: '0px 0px -50px 0px'
  };
  
  const observer = new IntersectionObserver((entries) => {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        entry.target.style.opacity = '1';
        entry.target.style.transform = 'translateY(0)';
      }
    });
  }, observerOptions);
  
  const cards = document.querySelectorAll('.card');
  cards.forEach((card, index) => {
    card.style.opacity = '0';
    card.style.transform = 'translateY(30px)';
    card.style.transition = 'opacity 0.6s ease, transform 0.6s ease';
    card.style.transitionDelay = `${index * 0.1}s`;
    observer.observe(card);
  });
  
  window.addEventListener('scroll', () => {
    const scrolled = window.pageYOffset;
    const header = document.querySelector('header');
    if (header) {
      header.style.transform = `translateY(${scrolled * 0.5}px)`;
    }
  });
  
  cards.forEach(card => {
    card.addEventListener('mouseenter', () => {
      if (navigator.vibrate) {
        navigator.vibrate(50);
      }
    });
    
    const link = card.querySelector('a');
    if (link) {
      card.addEventListener('mousemove', (e) => {
        const rect = card.getBoundingClientRect();
        const x = e.clientX - rect.left;
        const y = e.clientY - rect.top;
        const centerX = rect.width / 2;
        const centerY = rect.height / 2;
        const rotateX = (y - centerY) / centerY;
        const rotateY = (x - centerX) / centerX;
        
        link.style.transform = `translate(${rotateY * 5}px, ${rotateX * 5}px) translateY(-2px)`;
      });
      
      card.addEventListener('mouseleave', () => {
        link.style.transform = 'translate(0, 0)';
      });
    }
  });
  
  const title = document.querySelector('header h1');
  if (title) {
    setTimeout(() => {
      const currentLang = localStorage.getItem('lang') || 'en';
      const text = translations[currentLang].main_title;
      title.textContent = '';
      title.style.borderRight = '2px solid #00ffc8';
      
      let i = 0;
      const typeWriter = () => {
        if (i < text.length) {
          title.textContent += text.charAt(i);
          i++;
          setTimeout(typeWriter, 100);
        } else {
          setTimeout(() => {
            title.style.borderRight = 'none';
          }, 1000);
        }
      };
      typeWriter();
    }, 500);
  }
  
  createParticles();
});

function createParticles() {
  const particleContainer = document.createElement('div');
  particleContainer.style.position = 'fixed';
  particleContainer.style.top = '0';
  particleContainer.style.left = '0';
  particleContainer.style.width = '100%';
  particleContainer.style.height = '100%';
  particleContainer.style.pointerEvents = 'none';
  particleContainer.style.zIndex = '-1';
  document.body.appendChild(particleContainer);
  
  for (let i = 0; i < 20; i++) {
    const particle = document.createElement('div');
    particle.style.position = 'absolute';
    particle.style.width = '2px';
    particle.style.height = '2px';
    particle.style.backgroundColor = 'rgba(0, 255, 200, 0.3)';
    particle.style.borderRadius = '50%';
    particle.style.left = Math.random() * 100 + '%';
    particle.style.top = Math.random() * 100 + '%';
    particle.style.animation = `float ${3 + Math.random() * 4}s ease-in-out infinite`;
    particle.style.animationDelay = Math.random() * 2 + 's';
    particleContainer.appendChild(particle);
  }
}

document.querySelectorAll('a[href^="#"]').forEach(anchor => {
  anchor.addEventListener('click', function (e) {
    e.preventDefault();
    const target = document.querySelector(this.getAttribute('href'));
    if (target) {
      target.scrollIntoView({
        behavior: 'smooth',
        block: 'start'
      });
    }
  });
});